import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useArticles } from '../data/articles';
import ReactMarkdown from 'react-markdown';

const HomeContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
`;

const ArticleHeader = styled.div`
  margin-bottom: 2rem;

  h1 {
    margin-bottom: 0.5rem;
  }

  .meta {
    color: #666;
    font-size: 0.9rem;
  }
`;

const ArticleContent = styled.div`
  line-height: 1.6;

  h1, h2, h3, h4, h5, h6 {
    margin-top: 2rem;
    margin-bottom: 1rem;
  }

  p {
    margin-bottom: 1rem;
  }

  ul, ol {
    margin-bottom: 1rem;
    padding-left: 2rem;
  }
`;

const ArticleNavigation = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 3rem;
  padding-top: 2rem;
  border-top: 1px solid #eee;
`;

const NavLink = styled(Link)`
  text-decoration: none;
  color: #007bff;
  padding: 0.5rem 1rem;
  border: 1px solid #007bff;
  border-radius: 4px;
  transition: all 0.3s ease;

  &:hover {
    background-color: #007bff;
    color: white;
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

function Home() {
  const { articles, loading } = useArticles();

  if (loading) {
    return <HomeContainer>加载中...</HomeContainer>;
  }

  if (!articles || articles.length === 0) {
    return <HomeContainer>暂无文章</HomeContainer>;
  }

  // 获取最新的文章
  const currentArticle = articles[0];

  // 获取上一篇和下一篇文章的索引
  const currentIndex = 0; // 因为我们显示的是最新的文章，所以索引是0
  const prevArticle = articles[currentIndex + 1];
  const nextArticle = null; // 最新文章没有"下一篇"

  return (
    <HomeContainer>
      <ArticleHeader>
        <h1>{currentArticle.title}</h1>
        <div className="meta">
          <span>发布日期：{currentArticle.date}</span>
          <span> | </span>
          <span>分类：{currentArticle.category}</span>
        </div>
      </ArticleHeader>

      <ArticleContent>
        <ReactMarkdown>{currentArticle.content}</ReactMarkdown>
      </ArticleContent>

      <ArticleNavigation>
        {prevArticle ? (
          <NavLink to={`/article/${prevArticle.id}`}>
            ← 上一篇：{prevArticle.title}
          </NavLink>
        ) : (
          <div /> // 占位，保持布局
        )}
        {nextArticle && (
          <NavLink to={`/article/${nextArticle.id}`}>
            下一篇：{nextArticle.title} →
          </NavLink>
        )}
      </ArticleNavigation>
    </HomeContainer>
  );
}

export default Home;
