import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import ReactMarkdown from 'react-markdown';
import { useArticles } from '../data/articles';

const ArticleContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
`;

const ArticleHeader = styled.div`
  margin-bottom: 2rem;

  h1 {
    margin-bottom: 0.5rem;
  }

  .meta {
    color: #666;
    font-size: 0.9rem;
  }
`;

const ArticleContent = styled.div`
  line-height: 1.6;

  h1, h2, h3, h4, h5, h6 {
    margin-top: 2rem;
    margin-bottom: 1rem;
  }

  p {
    margin-bottom: 1rem;
  }

  ul, ol {
    margin-bottom: 1rem;
    padding-left: 2rem;
  }

  code {
    background-color: #f5f5f5;
    padding: 0.2em 0.4em;
    border-radius: 3px;
    font-family: monospace;
  }

  pre {
    background-color: #f5f5f5;
    padding: 1rem;
    border-radius: 5px;
    overflow-x: auto;

    code {
      background-color: transparent;
      padding: 0;
    }
  }
`;

function ArticleDetail() {
  const { id } = useParams();
  const { articles, loading } = useArticles();
  const [content, setContent] = useState('');

  const article = articles.find(a => a.id === parseInt(id));

  useEffect(() => {
    if (article) {
      import(`../data/articles/${article.path.split('/articles/')[1]}`)
        .then(module => {
          fetch(module.default)
            .then(response => response.text())
            .then(text => {
              const content = text.replace(/---\n[\s\S]*?\n---\n/, '');
              setContent(content);
            })
            .catch(error => {
              console.error('Error loading article:', error);
              setContent('文章加载失败');
            });
        })
        .catch(error => {
          console.error('Error importing article:', error);
          setContent('文章加载失败');
        });
    }
  }, [article]);

  if (loading) {
    return <ArticleContainer>加载中...</ArticleContainer>;
  }

  if (!article) {
    return <ArticleContainer>文章不存在</ArticleContainer>;
  }

  return (
    <ArticleContainer>
      <ArticleHeader>
        <h1>{article.title}</h1>
        <div className="meta">
          <span>发布日期：{article.date}</span>
          <span> | </span>
          <span>分类：{article.category}</span>
        </div>
      </ArticleHeader>
      <ArticleContent>
        <ReactMarkdown>{content}</ReactMarkdown>
      </ArticleContent>
    </ArticleContainer>
  );
}

export default ArticleDetail;
