import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { getArticlesByYear } from '../data/articles';

const ArchivesContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
`;

const ArticleLink = styled(Link)`
  text-decoration: none;
  color: #333;
  &:hover {
    color: #007bff;
  }
`;

function Archives() {
  const articlesByYear = getArticlesByYear();

  return (
    <ArchivesContainer>
      <h1>文章归档</h1>
      {Object.entries(articlesByYear)
        .sort(([yearA], [yearB]) => yearB - yearA)
        .map(([year, articles]) => (
          <div key={year}>
            <h2>{year}年</h2>
            <ul>
              {articles.map(article => (
                <li key={article.id}>
                  <ArticleLink to={`/article/${article.id}`}>
                    {article.date.substring(5)} - {article.title}
                  </ArticleLink>
                </li>
              ))}
            </ul>
          </div>
        ))}
    </ArchivesContainer>
  );
}

export default Archives;
