import styled from 'styled-components';
import { useParams, useNavigate } from 'react-router-dom';

const ArticleContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
`;

const ArticleHeader = styled.div`
  margin-bottom: 2rem;

  h1 {
    margin-bottom: 0.5rem;
  }

  .meta {
    color: #666;
    font-size: 0.9rem;
  }
`;

const ArticleContent = styled.div`
  line-height: 1.8;
`;

const BackButton = styled.button`
  margin-top: 2rem;
  padding: 0.5rem 1rem;
  background-color: #1e3c72;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #2a5298;
  }
`;

function ArticleDetail() {
  const { id } = useParams();
  const navigate = useNavigate();

  // 模拟文章数据
  const articles = [
    {
      id: "1",
      title: 'React Router 使用指南',
      date: '2024-03-20',
      content: '详细介绍 React Router 的使用方法...',
      category: '技术'
    },
    {
      id: "2",
      title: 'JavaScript 异步编程',
      date: '2024-02-15',
      content: '探讨 JavaScript 中的异步编程模式...',
      category: '技术'
    },
    {
      id: "3",
      title: '2023年度总结',
      date: '2023-12-10',
      content: '回顾2023年的技术发展...',
      category: '总结'
    }
  ];

  const article = articles.find(a => a.id === id) || {
    title: '文章未找到',
    date: '',
    content: '抱歉，未找到该文章',
    category: ''
  };

  return (
    <ArticleContainer>
      <ArticleHeader>
        <h1>{article.title}</h1>
        <div className="meta">
          <span>发布日期：{article.date}</span>
          <span style={{ margin: '0 10px' }}>|</span>
          <span>分类：{article.category}</span>
        </div>
      </ArticleHeader>
      <ArticleContent>
        {article.content}
      </ArticleContent>
      <BackButton onClick={() => navigate(-1)}>
        返回上一页
      </BackButton>
    </ArticleContainer>
  );
}

export default ArticleDetail;
