import { useEffect, useState } from 'react';

// 使用 require.context 动态导入所有markdown文件
const markdownFiles = require.context('./articles', true, /\.md$/);

// 解析markdown文件的frontmatter
const parseFrontmatter = (content) => {
  const match = content.match(/---\n([\s\S]*?)\n---/);
  if (!match) return null;

  const frontmatter = {};
  const lines = match[1].split('\n');
  lines.forEach(line => {
    const [key, ...valueParts] = line.split(':');
    if (key && valueParts.length) {
      frontmatter[key.trim()] = valueParts.join(':').trim();
    }
  });
  return frontmatter;
};

// 从文件路径中提取年份和月份
const extractDateFromPath = (path) => {
  const match = path.match(/\/(\d{4})\//);
  if (!match) return null;
  const year = parseInt(match[1]);
  return { year };
};

// 加载所有文章数据
const loadArticles = async () => {
  const articles = await Promise.all(
    markdownFiles.keys().map(async (path, index) => {
      const fileContent = await fetch(markdownFiles(path).default).then(res => res.text());
      const frontmatter = parseFrontmatter(fileContent);
      const dateInfo = extractDateFromPath(path);

      if (!frontmatter || !dateInfo) return null;

      return {
        id: index + 1,
        title: frontmatter.title,
        date: frontmatter.date,
        category: frontmatter.category,
        path: path.replace('./', '/articles/'),
        summary: fileContent.split('\n').slice(6, 7).join(' ').replace('#', '').trim(),
        year: dateInfo.year,
        month: parseInt(frontmatter.date.split('-')[1])
      };
    })
  );

  return articles.filter(article => article !== null);
};

// 获取按年份分组的文章
export const getArticlesByYear = (articles) => {
  const groupedArticles = {};
  articles.forEach(article => {
    if (!groupedArticles[article.year]) {
      groupedArticles[article.year] = [];
    }
    groupedArticles[article.year].push(article);
  });
  return groupedArticles;
};

// 获取按分类分组的文章
export const getArticlesByCategory = (articles) => {
  const groupedArticles = {};
  articles.forEach(article => {
    if (!groupedArticles[article.category]) {
      groupedArticles[article.category] = [];
    }
    groupedArticles[article.category].push(article);
  });
  return groupedArticles;
};

// 自定义Hook来获取文章数据
export const useArticles = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadArticles().then(loadedArticles => {
      setArticles(loadedArticles);
      setLoading(false);
    });
  }, []);

  return { articles, loading };
};

export default useArticles;
