import { useEffect, useState } from 'react';

// 自动导入所有markdown文件
const importAll = (r) => {
  let files = {};
  r.keys().forEach((key) => {
    files[key] = r(key);
  });
  return files;
};

// 使用 require.context 自动导入 articles 目录下的所有 .md 文件
const markdownFiles = importAll(
  require.context('./articles', true, /\.md$/)
);

// 解析markdown文件的frontmatter
const parseFrontmatter = (content) => {
  const match = content.match(/---\n([\s\S]*?)\n---/);
//   console.log('######获取文章',content,);
  if (!match) return null;

  const frontmatter = {};
  const lines = match[1].split('\n');
  lines.forEach(line => {
    const [key, ...valueParts] = line.split(':');
    if (key && valueParts.length) {
      frontmatter[key.trim()] = valueParts.join(':').trim();
    }
  });

  return frontmatter;
};

// 从文件路径中提取年份
const extractDateFromPath = (path) => {
  const match = path.match(/\/(\d{4})\//);
  if (!match) return null;
  const year = parseInt(match[1]);
  return { year };
};

// 加载所有文章数据
const loadArticles = async () => {
  const articles = await Promise.all(
    Object.entries(markdownFiles).map(async ([path, module], index) => {
      try {
        // 直接获取markdown内容，不需要fetch
        const fileContent = module.default;

        // 解析frontmatter
        const frontmatterMatch = fileContent.match(/---\s*\n([\s\S]*?)\n---/);
        if (!frontmatterMatch) {
          console.error(`No frontmatter found in ${path}`);
          return null;
        }

        const frontmatterContent = frontmatterMatch[1];
        const frontmatter = {};

        // 更严格的frontmatter解析
        frontmatterContent.split('\n').forEach(line => {
          const trimmedLine = line.trim();
          if (trimmedLine) {
            const colonIndex = trimmedLine.indexOf(':');
            if (colonIndex !== -1) {
              const key = trimmedLine.slice(0, colonIndex).trim();
              const value = trimmedLine.slice(colonIndex + 1).trim();
              frontmatter[key] = value;
            }
          }
        });

        // 验证必要的frontmatter字段
        if (!frontmatter.title || !frontmatter.date || !frontmatter.category) {
          console.error(`Missing required frontmatter fields in ${path}`, frontmatter);
          return null;
        }

        // 提取年份信息
        const dateInfo = extractDateFromPath(path);
        if (!dateInfo) {
          console.error(`Could not extract date info from path: ${path}`);
          return null;
        }

        // 提取文章内容（移除frontmatter）
        const content = fileContent.replace(/---\s*\n[\s\S]*?\n---\s*\n/, '').trim();

        // 提取摘要（第一段）
        const summary = content.split('\n')[0].replace(/^#\s*/, '').trim();

        return {
          id: index + 1,
          title: frontmatter.title,
          date: frontmatter.date,
          category: frontmatter.category,
          path: path,
          content: content,
          summary: summary,
          year: dateInfo.year,
          month: parseInt(frontmatter.date.split('-')[1])
        };
      } catch (error) {
        console.error(`Error processing article ${path}:`, error);
        return null;
      }
    })
  );

  // 过滤无效文章并按日期排序
  const validArticles = articles
    .filter(article => article !== null)
    .sort((a, b) => new Date(b.date) - new Date(a.date));

  console.log('Processed articles:', validArticles);
  return validArticles;
};

// 获取按年份分组的文章
export const getArticlesByYear = (articles) => {
  const groupedArticles = {};
  articles.forEach(article => {
    if (!groupedArticles[article.year]) {
      groupedArticles[article.year] = [];
    }
    groupedArticles[article.year].push(article);
  });
  return groupedArticles;
};

// 获取按分类分组的文章
export const getArticlesByCategory = (articles) => {
  const groupedArticles = {};
  articles.forEach(article => {
    if (!groupedArticles[article.category]) {
      groupedArticles[article.category] = [];
    }
    groupedArticles[article.category].push(article);
  });
  return groupedArticles;
};

// 自定义Hook来获取文章数据
export const useArticles = () => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadArticles()
      .then(loadedArticles => {
        console.log('Loaded articles:', loadedArticles); // 添加日志
        setArticles(loadedArticles);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error in useArticles:', error);
        setLoading(false);
      });
  }, []);

  return { articles, loading };
};

export default useArticles;
